    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        
        FacturarlosWSDL ClienteWSDL = new FacturarlosWSDL();
        
        /******************************************************/
        /*** true -> Firma en producci贸n **********************/
        ClienteWSDL.setEsProduccion(false);
        /******************************************************/
        /******************************************************/
        
        ClienteWSDL.setXmldocInFilePath(edtFacturaIn.getText()); // "./InputFiles/fact.xml"
        ClienteWSDL.setOutputXmlPDFPath("./OutputFiles/"); 
        
        ResultWSDL ResultFirma = ClienteWSDL.Execute_Firma();
        
        mmRespuesta.setText("");
        mmRespuesta.append(String.valueOf((  ResultFirma.getCodigoResp() )));
        mmRespuesta.append("\n"+             ResultFirma.getTextoResp()  );
        
        mmRespuestaXML.append(     "----------------------------------");
        mmRespuestaXML.append("\n"+"------- FIRMANDO ----------");
        mmRespuestaXML.append("\n"+"----------------------------------");
        mmRespuestaXML.append( "\n"+"FechaEmision: \t \t" +          ResultFirma.getFechaEmision() );
        mmRespuestaXML.append( "\n"+"FechaCertificacion: \t" +  ResultFirma.getFechaCertificacion() );
        mmRespuestaXML.append( "\n"+"NumeroAutorizacion: \t" +  ResultFirma.getNumeroAutorizacion() );
        mmRespuestaXML.append( "\n"+"Serie: \t \t" +            ResultFirma.getSerie() );
        mmRespuestaXML.append( "\n"+"Numero: \t \t" +           ResultFirma.getNumero() );
        mmRespuestaXML.append( "\n"+"PathXmlFile: \t \t" +      ResultFirma.getPathXmlFile() );
        mmRespuestaXML.append( "\n"+"PathPdfFile: \t \t" +      ResultFirma.getPathPdfFile() );
        
        
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        
        FacturarlosWSDL ClienteWSDL = new FacturarlosWSDL();
        
        /******************************************************/
        /*** true -> Anula en producci贸n **********************/
        ClienteWSDL.setEsProduccion(false);
        /******************************************************/
        /******************************************************/
        
        
        
        ClienteWSDL.setNumAutorizacionUuid(edtNumAutorizacionUUID.getText() );
        ClienteWSDL.setMotivoAnulacion(edtMotivoAnulacion.getText());
        
        ClienteWSDL.setOutputXmlPDFPath("./OutputFilesAnulacion/"); 
        
        ResultWSDL ResultAnulacion = ClienteWSDL.Execute_Anulacion();
        
        mmRespuesta.setText("");
        
        mmRespuesta.append(String.valueOf((  ResultAnulacion.getCodigoResp() )));
        mmRespuesta.append("\n"+             ResultAnulacion.getTextoResp()  );
        
        mmRespuestaXML.append("\n"+"----------------------------------");
        mmRespuestaXML.append("\n"+"------- ANULANDO ---------");
        mmRespuestaXML.append("\n"+"----------------------------------"); 
        mmRespuestaXML.append("\n"+"FechaAnulacion: \t" +        ResultAnulacion.getFechaAnulacion() );
        mmRespuestaXML.append("\n"+"FechaCertificacion: \t" +  ResultAnulacion.getFechaCertificacion() );

        
    }//GEN-LAST:event_jButton2ActionPerformed

